import React, { useState } from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import { Container } from "react-bootstrap";
import styled from "styled-components";
import { useHistory } from "react-router-dom";


const LoginStyle = styled.div`
@media all and (min-width: 480px) {
    .Login {
      padding: 60px 0;
    }

    .Login form {
      margin: 0 auto;
      max-width: 420px;
    }
  }
`;


type FormData = {
    quizId: string;
    quizName: string;
  }

  type FormErrors = {
    id?: string;
    name?: string;
  }

export function Login() {
    const history = useHistory();
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [errors, setErrors] = useState<FormErrors>({});

    function validateForm() {
      return email.length > 0 && password.length > 0;
    }

    function handleSubmit(event : React.FormEvent) {
      event.preventDefault();
    }

    return (
        <LoginStyle>
        <Container className="justify-content-center">
            <form onSubmit={onSubmit}>
                <h1>Edit Quiz</h1>
                <div >
                <label  htmlFor="quizId">
                    Quiz ID:
                </label>
                <input
                    id="quizId"
                    name={"quizId"}
                    value={state.quizId}
                    onChange={handleInputId}
                />
                {errors.id ? <span style={{color: "red"}}>{errors.id}</span> : null}
                </div>
                <div >
                <label  htmlFor="quizName">
                    Quiz name:
                </label>
                <input id="quizName" name={"quizName"} value={state.quizName} onChange={handleInputName} />
                {errors.name ? <span style={{color: "red"}}>{errors.name}</span> : null}
                </div>
                <div
                style={{
                    display: "flex",
                    justifyContent: "space-evenly",
                    alignItems: "center",
                }}
                >
                <Button className="btn-primary" type="submit">
                    Save
                </Button>
                <Button className="btn-secondary" onClick={ () => history.goBack()}>Cancel</Button>
                </div>
            </form>
        </Container>
        </LoginStyle>
    );
  }
