import React, { useEffect, useState } from 'react';
import { Button, Container } from 'react-bootstrap';
import { Link, RouteComponentProps, useLocation, useParams } from 'react-router-dom';
import styled from 'styled-components';
import { getProductWithIdAPI, Product } from '../apiHelper';
import ProductItem from './ProductItem';

type TParams = {
   productID: string
}

export const DetailedProduct = ({match} : RouteComponentProps<TParams>) => {

   const productID = match.params.productID
   console.log(productID)

   const [product, setProduct] = useState<Product>({name:"", price: "", tags: "", description: "", img: ""});
   const [error, setError] = useState(false);

   const addToBasket = () => {

   } 

   useEffect(() => { 
      getProductWithIdAPI(productID)
      .then((data : Product)=> {
         //console.log(data)
         setProduct(data);
         console.log(data)
      }).catch((e) => {
         setError(e.message);
      })
   },[])

   // const product = {
   //    name:"Cosrx - Pure Fit Cica Cream",
   //    price:"219 dkk",
   //    tags:"new-cosrx-combined-cream-pure-fit-cica",
   //    description:"Moisturising face cream that minimizes impurities, redness and acne scars",
   //    img:"images/products/cosrx-combined-cream-cosrx_-_pure_fit_cica_cream.jpg"
   // }

 return (
   <div>
    <h2>Detailed Product</h2>
    <div className="container">
      <div className="row">
            <div className="col-xs-3 item-photo" style={{maxWidth:"50%"}}>
                  <img
                  className="product-image medium"
                  src={product !== undefined ? process.env.PUBLIC_URL + product.img : ""}
                  alt={product?.name}
                  />
            </div>
               <div className="col-xs-5" style={{border:"0px solid gray"}}>
                     <h3 className="product-title">{product?.name}</h3>    
                     {/* <h4 className="product-brand" style={{color:"#337ab7"}}>{ product?.name.split("-")[0] }</h4> */}

                     <h4 className="title-price"><small>Price</small></h4>
                     <h3 className="product-price" style={{marginTop:"0px"}}> {product?.price} </h3>

                     <td className="actions">

                     <Link to={"/basket"}><button className="btn btn-lg btn-primary text-uppercase add" onClick={addToBasket}> Buy now </button></Link>
                     <button onClick={addToBasket} className="btn btn-lg btn-outline-primary text-uppercase add"> <i className="fas fa-shopping-cart"></i> Add to basket </button>                              
                     </td>
               </div>                              
            <div id="descriptionBox" className="col-xs-9">
               <div style={{width:"100%"}}>
                  <div style={{padding:"15px"}}>
                        <h4>Description</h4>
                  </div>
               </div>
               <div>
                  <p id="descriptionText">{product?.description}</p>    
               </div>	
            </div>     
       </div>   
      </div>   
   </div>   
 );
};
