import React, { useContext } from 'react';
import { getProductWithId, Product, removeFromBasket } from '../apiHelper';
import { ShopContext } from '../App';


export const Basket = () => {

   const  shopContext = useContext(ShopContext)
   // check conext
   if (!shopContext)
      throw(new Error("ShopContext is undefined!"))

   // deconstruct context to get quiz
   const { currentUser, annoymousBasket } = shopContext;

   const basket = currentUser == undefined ? annoymousBasket : currentUser.basket;

 return (
   <>
      <div className="hero is-primary">
        <div className="hero-body container">
            { currentUser == undefined ?
               <h4 className="title">My Basket</h4> :
               <h4 className="title">{currentUser.name}'s' Basket</h4>
            }
        </div>
      </div>
      <br />
      <div className="container">
        {basket.length ? (
          <div className="column columns is-multiline">
            {basket.map(key => (
              <CartItem
                product={getProductWithId(key)}
              />
            ))}
            <div className="column is-12 is-clearfix">
              <br />
              <div className="is-pulled-right">
                {/* <button
                  onClick={props.context.clearCart}
                  className="button is-warning "
                >
                  Clear cart
                </button>{" "} */}
                <button
                  className="button is-success">
                  Checkout
                </button>
              </div>
            </div>
          </div>
        ) : (
          <div className="column">
            <div className="title has-text-grey-light">No item in cart!</div>
          </div>
        )}
      </div>
    </>
 )};

 const CartItem = ({product} : {product: Product}) => {
   return (
     <div className=" column">
       <div className="box">
         <div className="media">
           <div className="media-left">
             <figure className="image is-64x64">
               <img
                 src={product.img}
                 alt={product.name}
               />
             </figure>
           </div>
           <div className="media-content">
             <b style={{ textTransform: "capitalize" }}>
               {product.name}{" "}
             </b>
             <small>{product.price}</small>
           </div>
           <div
             className="media-right"
             onClick={() => removeFromBasket(product.tags)}
           >
             <span className="delete is-large"></span>
           </div>
         </div>
       </div>
     </div>
   );
 };
