import React, { useContext, useEffect, useState } from 'react';
import { Button, Card, CardDeck, Col, Container, Dropdown, ListGroup, Nav, Row, Table } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { addToBasketAPI, getProductsAPI, Product,  } from '../apiHelper';
import { ShopContext } from '../App';
import LoadingBox from './LoadingBox';
import MessageBox from './MessageBox';

const filter_products = (filterValue: string) => {


   return undefined
}


const addToBasket = (tags: string) => {
   return undefined;
}

const buy = (tags: string) => {
   return undefined;
}


export const Products = () => {
   const  shopContext = useContext(ShopContext)
   // check conext
  if (!shopContext)
  throw(new Error("QuizContext is undefined!"))

   // deconstruct context to get quiz
   const { currentUser, categories, annoymousBasket } = shopContext

   const [products, setProducts] = useState<Product[]>([]);
   const [loading, setLoading] = useState(false);
   const [error, setError] = useState(false);

   useEffect( () => {
      setLoading(true);
      getProductsAPI("http://localhost:5000/products")
      .then((data)=> {
         console.log(data)
         setLoading(false);
         setProducts(data);
      }).catch((e) => {
         setError(e.message);
         setLoading(false);
      })

   }, []);

 return (
    <Container>
       <Row>
         <h2>Products</h2>
      </Row>
      <Row>
         <FilterBar></FilterBar>
      </Row>
      <Row>
         <div>
            {loading ? (<LoadingBox></LoadingBox>)
            :
               error? (<MessageBox variant="danger">{error}</MessageBox> )
            : (
            <div className="row center">
                  {products.map((product) => (
                     <ProductItem key={product.tags} product={product}></ProductItem>
                  ))}
            </div> 
            )}
         </div>
      </Row>
    </Container>
 );
};

function ProductItem(props: any) {
   const product = props.product;
   return (
      <div key={product.tags} className="card">
         <Link to={`/products/${product.tags}`}>
         <img
            className="product-image medium"
            src={process.env.PUBLIC_URL + product.img}
            alt={product.name}
         />
         </Link>
         <div className="card-body">
            <Link to={`/products/${product.tags}`}>{product.name}</Link>
            <div className="product-brand">{product.name.split("-")[0] }</div>
            <div className="product-price">{product.price}</div>
            <ButtonStyle><button className="btn btn-outline-primary" onClick={addToBasket(product.tags)}>Add to basket</button></ButtonStyle>
            <ButtonStyle><button className="btn btn-outline-primary">Buy</button></ButtonStyle>
         </div>

      </div>
   );
}

const ButtonStyle = styled.button`
.button {
   position: fixed;
   bottom: 0;
   left: 0;
}
 `;


const filterBarStyle = {
   display: 'flex',
   alignItems: 'center',

 };

const FilterBar = () => {
   return (
        <div style={filterBarStyle} >
            <Dropdown style={{margin:"8px 4px 8px 4px"}}>
               <Dropdown.Toggle variant="success" id="dropdown-basic">
                  Product Type
               </Dropdown.Toggle>
               <Dropdown.Menu>
                  <Dropdown.Item onClick={filter_products("cleanser")}>Cleanser</Dropdown.Item>
                  <Dropdown.Item onClick={filter_products("toner")}>Toner</Dropdown.Item>
                  <Dropdown.Item onClick={filter_products("serum")}>Serum</Dropdown.Item>
                  <Dropdown.Item onClick={filter_products("mask")}>Mask</Dropdown.Item>
                  <Dropdown.Item onClick={filter_products("cream")}>Cream</Dropdown.Item>
               </Dropdown.Menu>
            </Dropdown>
            <Dropdown style={{margin:"8px 4px 8px 4px"}}>
                  <Dropdown.Toggle variant="success" id="dropdown-basic">
                     Skin Type
                  </Dropdown.Toggle>
                  <Dropdown.Menu>
                     <Dropdown.Item onClick={filter_products("dry")}>Dry</Dropdown.Item>
                     <Dropdown.Item onClick={filter_products("oily")}>Oily</Dropdown.Item>
                     <Dropdown.Item onClick={filter_products("combined")}>Combined</Dropdown.Item>
                     <Dropdown.Item onClick={filter_products("sensitive")}>Sensitive</Dropdown.Item>
                  </Dropdown.Menu>
               </Dropdown>
            <Dropdown style={{margin:"8px 4px 8px 4px"}}>
                  <Dropdown.Toggle variant="success" id="dropdown-basic">
                     Brand
                  </Dropdown.Toggle>
                  <Dropdown.Menu>
                     <Dropdown.Item onClick={filter_products("cosrx")}>CosRX</Dropdown.Item>
                     <Dropdown.Item onClick={filter_products("dr. jart+")}>Dr. Jart+</Dropdown.Item>
                     <Dropdown.Item onClick={filter_products("mizon")}>Mizon</Dropdown.Item>
                     <Dropdown.Item onClick={filter_products("nature republic")}>Nature Republic</Dropdown.Item>
                  </Dropdown.Menu>
               </Dropdown>
        </div>
   )
}


