import React, { useContext, useEffect, useState } from 'react';
import { Button, Col, Container, Row } from 'react-bootstrap';
import { Link, RouteComponentProps, useLocation, useParams } from 'react-router-dom';
import styled from 'styled-components';
import { getProductWithIdAPI, addToBasketAPI, Product } from '../apiHelper';
import { ShopContext } from '../App';
import { Basket } from './Basket';
import LoadingBox from './LoadingBox';
import MessageBox from './MessageBox';
import ProductItem from './ProductItem';

export const DetailedProduct = (props : any) => {

   const  shopContext = useContext(ShopContext)
   // check conext
  if (!shopContext)
  throw(new Error("QuizContext is undefined!"))

   // deconstruct context to get quiz
   const { isLoggedIn, currentUser, updateCurrentUser, annoymousBasket, updateAnnoymousBasket } = shopContext


   const productID : string = props.match.params.productID
   //console.log(productID)
   const dummy : Product = {name: "", price: "", tags: "", img: "", description: ""};
   //console.log(productID)

   const [product, setProduct] = useState<Product>({name: "", price: "", tags: "", img: "", description: ""});
   const [loading, setLoading] = useState(false);
   const [error, setError] = useState(false);

   const addToBasket = () => {
      if( isLoggedIn ){
         addToBasketAPI(product.tags, currentUser.email)
         .then((responce) => {
            if(responce.ok) {
               const user = {
                  customerName: currentUser.customerName, 
                  email: currentUser.email, 
                  password: currentUser.password,
                  basket: currentUser.basket.concat([product])
               }
               updateCurrentUser(user)
            } else {
               // element could not be added
            }
         })} else {
            annoymousBasket.push(product)
         }
      }


   useEffect(() => { 
      setLoading(true);
      getProductWithIdAPI(productID)
      .then((data)=> {
         setLoading(false);
         setProduct(data);
         console.log(product)
      }).catch((e) => {
         setError(e.message);
         setLoading(false);
      })
   },[])

 return (
   <Container>
      <div>
            {loading ? (<LoadingBox></LoadingBox>)
            :
               error? (<MessageBox variant="danger">{error}</MessageBox> )
            : (
               <div>
                  <div>
                     <div className="container">
                        <div className="row">
                           <span className="col-xs-3 item-photo" style={{maxWidth:"50%", margin:"10%"}}>
                                 <img src={product.img} />
                           </span>
                           <span className="col-xs-5" style={{border:"0px solid gray"}}>
                                 <h3>Product title</h3>    
                                 <h4 style={{color:"#337ab7"}}>CosRX</h4>

                                 <h4 className="title-price"><small>Price</small></h4>
                                 <h3> 300 dkk </h3>

                                 <td className="actions">
                                 <a onClick={()=>addToBasket} href="basket.html" className="btn btn-lg btn-primary text-uppercase add"> Buy now </a>
                                 <a onClick={()=>addToBasket} className="btn btn-lg btn-outline-primary text-uppercase add"> <i className="fas fa-shopping-cart"></i> Add to cart </a>                                   
                                 </td>
                              </span>                              
                        </div>
                     </div>	
                     <div id="descriptionBox" className="col-xs-9">
                           <div style={{width:"100%"}}>
                              <p style={{padding:"15px"}}>
                                    <h4>Description</h4>
                              </p>
                           </div>
                           <div>
                              <p>{product.description}</p>    
                           </div>	
                        </div>    
                     </div>
                     {/* <Link to="/products"> Back to products</Link>
                     <div key={product?.tags} style={{padding:"10%"}} className="row ">
                              <div className="col-2">
                                 <img
                                    className="large"
                                    src={`${product.img}`}
                                    alt={product.name}
                                 />
                              </div>

                              <div className="col-1" style={{width:"fit-contet"}}>
                                 <ul style={{listStyleType:"none"}}>
                                    <li>
                                       <h2>{product.name}</h2>
                                    </li>
                                    <li>
                                       <h3>{product.price}</h3>
                                    </li>
                                    <li>
                                       <h3>Description</h3>
                                       <p>{product.description }</p>
                                    </li>
                                    <li>
                                       <Button className="primary block" onClick={()=> addToBasket}>Add to basket</Button>
                                    </li>
                                 </ul>
                           </div> 
                              </div> */}

               </div>
               )}
      </div>
   </Container>   
 );
};
