import React from 'react';
import { Button, Container } from 'react-bootstrap';
import styled from 'styled-components';

const ProductStyle = styled.div`
.navbar {
   list-style: none;
}

.default {
   border-color: #e7e7e7;
   color: black;
}

.default:hover {
   background: #e7e7e7;
}

.img {
   height: fit-content;
   width: fit-content;
}

.container-fluid {
   margin-top: 30px;
   display: grid;
   grid-gap: 30px;
   grid-template-columns: repeat(12, 1fr);
   grid-template-rows: repeat(100px auto 100px);
   grid-template-areas: 
       "m m h h h h h h h h h h"
       "m m p p p p p p p p p p";
}

.header {
   grid-area: h;
}

.filter {
   grid-area: m;
}


.button {
   position: fixed;
   bottom: 0;
   left: 0;
}

.products {
   grid-area: p;
   display: grid;
   grid-gap: 10px;
   grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
   grid-auto-rows: auto-fit;
   /*display: flex;*/
   flex-wrap: wrap;
}


img{
   max-width: 100%;
   max-height: 100%;
   display: block; /* remove extra space below image */
}


.descriptionText {
   color: black;

   text-align: center;
 }
`;


export const Products = () => {

const filter_products = (e: React.InputHTMLAttributes<HTMLInputElement>) => {}
const add_to_cart = (e: React.ChangeEvent<HTMLInputElement>) => {}
const addAndNavigateToBasket = (e: React.ChangeEvent<HTMLInputElement>) => {}
const navigate_to_details = (e: React.ChangeEvent<HTMLInputElement>) => {}

 return (
    <ProductStyle>
      <Container>
         <div className="container-fluid mt-100">  
            <div className="filter">
               <article className="filter-group" id="filter-group">
                  <header className="card-header"> <a href="#" data-toggle="collapse" data-target="#collapse_aside1" data-abc="true" aria-expanded="false" className="collapsed"> <i className="icon-control fa fa-chevron-down"></i>
                           <h6 className="title">Product Type </h6>
                        </a> </header>
                  <div className="filter-content collapse" id="collapse_aside1">
                        <div className="card-body">
                           <label className="checkbox-btn"> <input className="productType" type="checkbox" onChange={filter_products}/> <span className="btn btn-light"> Cleanser </span> </label>
                           <label className="checkbox-btn"> <input className="productType" type="checkbox" onChange={filter_products}/> <span className="btn btn-light"> Toner </span> </label>
                           <label className="checkbox-btn"> <input className="productType" type="checkbox" onChange={filter_products}/> <span className="btn btn-light"> Serum </span> </label>
                           <label className="checkbox-btn"> <input className="productType" type="checkbox" onChange={filter_products}/> <span className="btn btn-light"> Mask </span> </label>
                           <label className="checkbox-btn"> <input className="productType" type="checkbox" onChange={filter_products}/> <span className="btn btn-light"> Cream </span> </label>
                        </div>
                  </div>
               </article>
               <article className="filter-group" id="filter-group">
                  <header className="card-header"> <a href="#" data-toggle="collapse" data-target="#collapse_aside2" data-abc="true" aria-expanded="false" className="collapsed"> <i className="icon-control fa fa-chevron-down"></i>
                           <h6 className="title">Skin Type</h6>
                        </a> </header>
                  <div className="filter-content collapse" id="collapse_aside2" >
                        <div className="card-body">
                        <label className="checkbox-btn"> <input className="skinType" type="checkbox" onChange={filter_products}/> <span className="btn btn-light"> Dry Skin </span> </label> 
                        <label className="checkbox-btn"> <input className="skinType" type="checkbox" onChange={filter_products}/> <span className="btn btn-light"> Oily Skin </span> </label> 
                        <label className="checkbox-btn"> <input className="skinType" type="checkbox" onChange={filter_products}/> <span className="btn btn-light"> Sensitive Skin </span> </label> 
                        <label className="checkbox-btn"> <input className="skinType" type="checkbox" onChange={filter_products}/> <span className="btn btn-light"> Combined Skin </span> </label> 
                        </div>
                  </div>
               </article>
               <article className="filter-group" id="filter-group" >
                  <header className="card-header"> <a href="#" data-toggle="collapse" data-target="#collapse_aside4" data-abc="true" aria-expanded="false" className="collapsed"> <i className="icon-control fa fa-chevron-down"></i>
                           <h6 className="title">Brands </h6>
                        </a> </header>
