import React from 'react';
import './App.css';

import { Header } from './components/header';
import { Footer } from './components/footer';
import { BrowserRouter, Route, Switch } from 'react-router-dom';
import { Products } from './components/products';
import { Login } from './components/login';
import { Register } from './components/register';
import { Basket } from './components/basket';
import { Home } from './components/home';
import { DetailedProduct } from './components/detailedProduct';
import { Category, getCategoriesAPI, User } from './apiHelper';


export interface IShopContext {
  currentUser: User | undefined;
  categories: Category[];
  annoymousBasket: string[];
}

// create context, but there is no default value - set it to undefined.
export const ShopContext = React.createContext<IShopContext | undefined>(undefined);

export const App = () => {

  const categories : Category[] = getCategoriesAPI();


  return (
    <ShopContext.Provider value= {{currentUser:undefined, categories:categories, annoymousBasket:[]}}>
      <div className="App">
        <BrowserRouter>
            <Header />
            <Switch>
                  <Route exact path="/products" component={() => <Products/>} />
                  <Route exact path="/products/:tags" component={() => <DetailedProduct/>} />
                  <Route exact path="/login" component={() => <Login/>} />
                  <Route exact path="/register" component={() => <Register/>} />
                  <Route exact path="/basket" component={Basket} />
                  <Route exact path="/home" component={Home} />
                  <Route path="/" component={Home} />
            </Switch>
            <Footer />
        </BrowserRouter>
      </div>
    </ShopContext.Provider>
  );
}

export default App;
