import React, { useContext, useEffect, useState } from 'react';
import { Button, Card, CardDeck, Col, Container, Dropdown, ListGroup, Nav, Row, Table } from 'react-bootstrap';
import styled from 'styled-components';
import { getProducts, Product,  } from '../apiHelper';
import { ShopContext } from '../App';
import { SidebarStyle } from '../styles';

const filter_products = (filterValue: string) => {


   return undefined
}


const addToCart = (tags: string) : void => {
   return undefined;
}

const buy = (tags: string): void => {
   return undefined;
}

const FilterBar = () => {
   return (
        <div className="flexbox-container">
            <Dropdown style={{margin:"4px 4px 4px 4px"}}>
               <Dropdown.Toggle variant="success" id="dropdown-basic">
                  Product Type
               </Dropdown.Toggle>
               <Dropdown.Menu>
                  <Dropdown.Item onClick={filter_products("cleanser")}>Cleanser</Dropdown.Item>
                  <Dropdown.Item onClick={filter_products("toner")}>Toner</Dropdown.Item>
                  <Dropdown.Item onClick={filter_products("serum")}>Serum</Dropdown.Item>
                  <Dropdown.Item onClick={filter_products("mask")}>Mask</Dropdown.Item>
                  <Dropdown.Item onClick={filter_products("cream")}>Cream</Dropdown.Item>
               </Dropdown.Menu>
            </Dropdown>
            <Dropdown style={{margin:"4px 4px 4px 4px"}}>
                  <Dropdown.Toggle variant="success" id="dropdown-basic">
                     Skin Type
                  </Dropdown.Toggle>
                  <Dropdown.Menu>
                     <Dropdown.Item onClick={filter_products("dry")}>Dry</Dropdown.Item>
                     <Dropdown.Item onClick={filter_products("oily")}>Oily</Dropdown.Item>
                     <Dropdown.Item onClick={filter_products("combined")}>Combined</Dropdown.Item>
                     <Dropdown.Item onClick={filter_products("sensitive")}>Sensitive</Dropdown.Item>
                  </Dropdown.Menu>
               </Dropdown>
            <Dropdown style={{margin:"4px 4px 4px 4px"}}>
                  <Dropdown.Toggle variant="success" id="dropdown-basic">
                     Brand
                  </Dropdown.Toggle>
                  <Dropdown.Menu>
                     <Dropdown.Item onClick={filter_products("cosrx")}>CosRX</Dropdown.Item>
                     <Dropdown.Item onClick={filter_products("dr. jart+")}>Dr. Jart+</Dropdown.Item>
                     <Dropdown.Item onClick={filter_products("mizon")}>Mizon</Dropdown.Item>
                     <Dropdown.Item onClick={filter_products("nature republic")}>Nature Republic</Dropdown.Item>
                  </Dropdown.Menu>
               </Dropdown>
        </div>
   )
}


export const Products = () => {
   const  shopContext = useContext(ShopContext)
   // check conext
  if (!shopContext)
  throw(new Error("QuizContext is undefined!"))

   // deconstruct context to get quiz
   const { currentUser, categories, annoymousBasket } = shopContext

   const [products, setProducts] = useState<Product[]>([]);
   useEffect( () => {
      getProducts("http://localhost:5000/products")
      .then((data)=> {
         setProducts(data);
      }).catch((e) => {

      })

   });

 return (
    <Container>
       <Row>
         <h2>Products</h2>
      </Row>
      <Row>
         <FilterBar></FilterBar>
      </Row>
      <Row>
         <Container style={{ display: "flex", justifyContent: "center" }}>
         {products.length ? (
            <CardDeck>
               {products.map((el, index) => (
                  <Card className="productcard" style={{ width: "180px" }}>
                  <Card.Img variant="top" src={el.img} />
                  <Card.Body>
                     <Card.Title>{el.name}</Card.Title>
                     <Card.Text>{el.price}</Card.Text>
                  </Card.Body>
                  <Button onClick={() => addToCart(el.tags)}>Add to cart</Button>
                  <Button onClick={() => buy(el.tags)}>Buy</Button>
                  </Card>
               ))}
            </CardDeck>
         ) : (
         <div className="column">
            <div className="title has-text-grey-light">No products were found</div>
         </div>
         )}
         </Container> 

      </Row>
    </Container>
 );
};
